const body=document.querySelector("body"),toggle=document.getElementById("toggle"),input=document.getElementById("switch");toggle.addEventListener("click",(function(){input.checked?(body.classList.remove("night"),toggle.classList.remove("active")):(body.classList.add("night"),toggle.classList.add("active"))}));const scrollUp=document.querySelector(".scroll-up"),offset=800,getTop=()=>window.pageYOffset||document.documentElement.scrollTop;window.addEventListener("scroll",()=>{(window.pageYOffset||document.documentElement.scrollTop)>800?scrollUp.classList.add("scroll-up--active"):scrollUp.classList.remove("scroll-up--active")}),scrollUp.addEventListener("click",()=>{window.scrollTo({top:0,behavior:"smooth"})});