const body=document.querySelector("body"),toggle=document.getElementById("toggle"),input=document.getElementById("switch");toggle.addEventListener("click",(function(){input.checked?body.classList.remove("night"):body.classList.add("night")}));const scrollUp=document.querySelector(".scroll-up"),offset=800,getTop=()=>window.pageYOffset||document.documentElement.scrollTop;window.addEventListener("scroll",()=>{(window.pageYOffset||document.documentElement.scrollTop)>offset?scrollUp.classList.add("scroll-up--active"):scrollUp.classList.remove("scroll-up--active")}),scrollUp.addEventListener("click",()=>{window.scrollTo({top:0,behavior:"smooth"})});const animItems=document.querySelectorAll(".animation");if(animItems.length>0){function animOnScroll(e){for(let e=0;e<animItems.length;e++){const t=animItems[e],o=t.offsetHeight,n=offset(t).top,l=4;let s=window.innerHeight-o/l;o>window.innerHeight&&(s=window.innerHeight-window.innerHeight/l),pageYOffset>n-s&&pageYOffset<n+o?t.classList.add("active"):t.classList.contains("animation-no")||t.classList.remove("active")}}function offset(e){const t=e.getBoundingClientRect(),o=window.pageXOffset||document.documentElement.scrollLeft,n=window.pageYOffset||document.documentElement.scrollTop;return{top:t.top+n,left:t.left+o}}window.addEventListener("scroll",animOnScroll),setTimeout(()=>{animOnScroll()},300)}